import React, {useCallback, useState} from 'react';
import PayPal from './components/PayPal';

const App = () => {
	const [checkout, setCheckout] = useState(false);

	const handleClickCheckout = useCallback(() => setCheckout(true), []);

	return (
		<>
			{
				checkout ? <PayPal /> : <button onClick={handleClickCheckout}>Checkout</button>
			}
		</>
	);
};

export default App;
